package com.ozgreat.calculator.model.util;

import com.ozgreat.calculator.model.*;

import java.math.BigDecimal;
import java.util.*;

import static com.ozgreat.calculator.model.ArithmeticOperation.*;
import static org.junit.jupiter.api.Assertions.*;

public class ModelTestUtil {
  protected final static String MAX_POS = ".";
  protected final static String MIN_POS = "0.";
  protected final static String MAX_POS_NEGATE = "-.";
  protected final static String MIN_POS_NEGATE = "-0.";
  protected final static String MAX_POS_HALF = "";
  protected final static String MAX_POS_HALF_NEGATE = "-";
  private static final List<String> opList = Arrays.asList("+", "-", "/", "*", "R", "POW", "√", "±", "=", "%+", "%*",
      "MC", "MS", "M+", "M-");
  private static final Map<String, ArithmeticOperation> operations = new HashMap<>();
  private static final Map<String, MemoryOperation> memoryOperation = new HashMap<>();

  static {
    operations.put("+", ADD);
    operations.put("-", SUBTRACT);
    operations.put("*", MULTIPLY);
    operations.put("/", DIVIDE);
    operations.put("POW", POW);
    operations.put("±", NEGATE);
    operations.put("√", SQRT);
    operations.put("%+", PERCENT_ADD_SUBTRACT);
    operations.put("%*", PERCENT_MUL_DIVIDE);
    operations.put("R", REVERSE);

    memoryOperation.put("MC", MemoryOperation.MEMORY_CLEAR);
    memoryOperation.put("M+", MemoryOperation.MEMORY_ADD);
    memoryOperation.put("M-", MemoryOperation.MEMORY_SUB);
    memoryOperation.put("MS", MemoryOperation.MEMORY_SAVE);
  }

  protected CalculatorModel calc;

  private BigDecimal patternRun(String pattern) throws CalculatorException {
    pattern = patternBuilder(pattern);
    BigDecimal x = BigDecimal.ZERO;
    String[] split = pattern.split(" ");
    for (int i = 0; i < split.length; i++) {
      String s = split[i];
      if (s.isBlank()) {
        continue;
      }
      if (operations.containsKey(s)) {
        x = calc.calculate(operations.get(s));
      } else if (memoryOperation.containsKey(s)) {
        x = calc.calculateMemory(memoryOperation.get(s));
      } else {
        if (i + 1 != split.length && operations.containsKey(split[i + 1])
            && operations.get(split[i + 1]).getType() == ArithmeticOperationType.UNARY) {
          x = calc.calculate(operations.get(split[i + 1]), new BigDecimal(s));
          x = calc.calculate(x);
          i++;
        } else {
          x = calc.calculate(new BigDecimal(s));
        }
      }
    }
    calc = new CalculatorModel();
    return x;
  }

  private String patternBuilder(String pattern) {
    pattern = pattern.replaceAll("\\s", "");
    StringBuilder patternBuilder = new StringBuilder(pattern);
    for (int i = pattern.length(); i > 0; --i) {
      String patternBuf = pattern.substring(0, i);
      opList.stream().filter(patternBuf::endsWith).max(Comparator.comparing(String::length)).
          ifPresent((String x) -> {
            int index = patternBuf.lastIndexOf(x);

            boolean nextIsBinaryOrPercentOp = patternBuf.substring(0, index).endsWith("-") ||
                patternBuf.substring(0, index).endsWith("+") || patternBuf.substring(0, index).endsWith("*") ||
                patternBuf.substring(0, index).endsWith("/") || patternBuf.substring(0, index).endsWith("-") ||
                patternBuf.substring(0, index).endsWith("MS") || patternBuf.substring(0, index).endsWith("M+") ||
                patternBuf.substring(0, index).endsWith("MC") || patternBuf.substring(0, index).endsWith("M-");

            boolean checkThatIsNotLastMinus = patternBuf.substring(0, index).isBlank();

            if (!(x.equals("-") && (nextIsBinaryOrPercentOp || checkThatIsNotLastMinus))) {
              patternBuilder.insert(index, " ");
              patternBuilder.insert(index + x.length() + 1, " ");
            }
          });
    }

    return patternBuilder.toString();
  }

  protected void checkOperations(String pattern, String expected) throws CalculatorException {
    BigDecimal x = patternRun(pattern);


//    System.out.println(x.toPlainString());
    assertEquals(0, x.compareTo(new BigDecimal(expected)));
  }

  protected void checkOperations(String pattern) throws CalculatorException {
    assertNull(patternRun(pattern));
  }

  protected void checkThrow(String pattern, CalculatorExceptionType expectedType) {
    try {
      patternRun(pattern);
    } catch (CalculatorException e) {
      assertEquals(expectedType, e.getType());
    }
  }
}
